<?php$flavor = $_REQUEST['flavor']; $count=0;$output=""; 						$arr=array("sweetMelissa" => "Sweet Melissa", "saltandvinegar"=> "Salt and Vinegar", "cajun" => "Cajun", 		"sour"=>"Sour Cream and Chive", "baltimore"=>"Baltimore Style", "davelinden"=> "Dave's Linden Ave",		"hellKat"=>" Hell Kat", "seasalt"=>"Taylor's Sea Salt", "cheddar"=>"Cheddar", "jalepeno"=> "Jalepeno",		"ranch"=>" Kath's Ranch", "bbq"=>"Barbeque"); 			require("../db.php");		               				$query = "SELECT votes FROM voting WHERE flavor=\"".$flavor."\"";			$result = $mysqli->query($query);				if ($mysqli->errno) {			$output .= $mysqli->error;			exit();		}		while ($row = $result->fetch_assoc()) {					foreach ($row as $type => $item) {				$count=$item;				}			} 		/**counts the votes of the the voting form on fans*/ 		$count++; 		 		$update="UPDATE voting SET votes=".$count. " WHERE flavor=\"".$flavor."\"";  		 		$updated=$mysqli->query($update); 		if ($mysqli->errno) {			$output .=$mysqli->error;			exit();		} 	 	/**gets the winner and prints it out*/ 		$theWinner=""; 		$winner="SELECT flavor FROM voting WHERE votes= (SELECT MAX(votes) from voting)"; 		$winning=$mysqli->query($winner); 		if ($mysqli->errno) {			$output .= $mysqli->error;			exit();			}		while ($row = $winning->fetch_assoc()) {					$output=" <div id='newresults'><p> Thanks for voting! Currently, "; 			foreach ($row as $type => $item) {				 $theWinner=$item;				$output.=$arr[$item]; 			}			$output.=" is winning! </p></div>"; 		}			if($flavor!=$theWinner){		$table="SELECT votes FROM voting WHERE flavor=\"".$flavor."\"";			$print=$mysqli->query($table); 		if ($mysqli->errno) {			$output .= $mysqli->error;			exit();			}		while ($row = $print->fetch_assoc()) {					$output.=" <div id='yourvote'><p> Your choice, ".$arr[$flavor].", has "; 			foreach ($row as $item) {								$output.=$item; 			if($item==1){				$output.=" vote! Come back and vote again!</p></div>"; 			}			else{			$output.=" votes! Come back and vote again!</p></div>"; 			}			}		}		}$output.=" <p id='scooter'>Don't forget to stop by and buy a raffle for the scooter!<br />					<img src='images/scooter.jpg' height='200' width= '250' alt='Scooter!'/></p>";print($output); 	?>