<?php	function frameworkGetContent() {		$output = "";								require_once("db.php");						//if the admin session is set then display the update information				if(isset($_SESSION['logged_user']) && $_SESSION['logged_user'] == 'Admin'){				//Adding New Employees		/* Pseudo Code			If a new employee is submitted				If a picture was upload					Make sure it was valid					Use SQL to add it to the photos table				Else					Print the upload was unsuccessful				Get photoID using SQL from the file's url				Strip user input of dangerous tags				Insert information into employees table using SQL		*/		$output .= "<div id='addemp'><h2>Add a new Employee</h2>						<form  id='addempform' action='?page=employees' method='post' enctype='multipart/form-data' >";					if (isset($_POST['newEmp'])) {				$check = true;								if (isset($_FILES['file'])) {					if ($_FILES['file']['error'] == UPLOAD_ERR_OK) {						move_uploaded_file($_FILES['file']['tmp_name'], "images/".$_FILES['file']['name']);						$_SESSION['photos'] = "uploads/".$_FILES['file']['name'];						$query = "INSERT INTO photos(url, category) VALUES('".strip_tags($_FILES['file']['name'])."', 'employees')";						$mysqli->query($query);											} else {						$output .= "\nThe file ".$_FILES['file']['name']." was not uploaded.\n";					}				}							$url = mysql_real_escape_string(strip_tags($_FILES['file']['name']));				$getpID = "SELECT photoID FROM photos WHERE url = '$url' AND category = 'employees'";        		$result= $mysqli->query($getpID);        		if (!$result) {               		$output .= $mysqli->error;                	exit();            	}       			$pID="";       			while ($row = $result->fetch_assoc()){                	foreach ($row as $type => $item) {               			$pID=$item;            		}      			}								if ($check) {					//Error checking					$name = mysql_real_escape_string(strip_tags($_POST["name"]));					$about = mysql_real_escape_string(strip_tags($_POST["about"]));					$about = str_replace(Chr(13),'<p>', $about);					$contact = mysql_real_escape_string(strip_tags($_POST["contact"]));										$query1 = "INSERT INTO employees(name, about, contact, photoID) VALUES('$name', '$about', '$contact', $pID)";					$mysqli->query($query1);					$output .= "The new employee was successfully added to the site!";				}			}						$output .= "<p> Name: <input type='text' name='name' id='name' /><span class='msg' id='namemsg'>*</span><br />					About: <textarea rows=9 cols=50 name='about' id='info' ></textarea><span class='msg' id='aboutmsg'>*</span><br />					E-mail: <input type='text' name='contact' id='email' /><span class='msg' id='emailmsg'>*</span><br />					Photo: <input type='file' name='file' id='photo' /><span class='msg' id='photomsg'>*</span>					</p>";						$output .= "<p>					<input type='submit' name='newEmp' value='Add a New Employee' />					<span class='msg'><span id='submitmsg'>&nbsp;</span></span>					</p>				</form></div>";				//Delete an Employee		/* Pseudo Code					*/  		$output .= "<div id='deleteemp'><h2>Delete an Employee</h2>					<form  id='deleteempform' action='?page=employees' method='post' >";						if(isset($_POST['deleteEmp'])) {				$emp = $_POST["emp"];				$check = true;				if($check) {					$query0 = "SELECT photoID FROM employees WHERE empID = '$emp'";					$result= $mysqli->query($query0);										while ($row = $result->fetch_assoc()){                		foreach ($row as $type => $item) {               				$pid = $item;            			}      				}				            		$query1= "DELETE FROM employees WHERE empID = '$emp'";            		$mysqli->query($query1);            		$check = false;            		            		$query2= "DELETE FROM photos WHERE photoID = $pid";            		$mysqli->query($query2);            		$check = false;				}			}						$emps = "SELECT name, empID FROM employees";        	$result= $mysqli->query($emps);        	if (!$result) {               	$output .= $mysqli->error;                exit();            }    			    		$output .= "Choose Employee: <select name='emp'>";    		$output .= "<option value='item'>Choose an Employee</option>";       		while ($row = $result->fetch_assoc()){                foreach ($row as $type => $item) {               		if($type == 'name'){                		$name = $item;                	}                	else{                		$id = $item;                	}            	}            	$output .= "<option value='$id'>$name</option>";      		}      		$output .= "</select><br />";      		      		$output .= "<p>      			<input type='submit' name='deleteEmp' value='Delete' id='delete' />				<span class='message'><span id='deletemsg'>&nbsp;</span></span>				</p>			</form></div>";						}				//logout		if (isset($_POST['logout'])) {			$olduser = $_SESSION['logged_user'];			unset($_SESSION['logged_user']);			session_destroy();		} else {			$olduser = false;		}		if (isset($_SESSION['logged_user'])) {			$output.= '<form class="logout" action="?page=login" method="post">			  <input id="button" type="submit" name="logout" value="Logout" />			  </form>';		}				//view employees		$query = "SELECT * FROM employees";		$result = $mysqli->query($query);		if ($mysqli->errno) {			$output .=$mysqli->error;			exit();		}		$output .= "<div id='fromdb'><div id=\"teamhead\">        <img src=\"images/team.png\" alt=\"team\" />      </div>";	  	$output .="<div class=\"output\">";				while ($row = $result->fetch_assoc()) {			$output .= "<div class='empline' id='".$row['empID']."'>";						foreach ($row as $type => $item) {					if($type == "name") {						$output .= "<div class='empname'>". $item . "</div>\n";					}					else if($type == "photoID") {						$output .= "<div class='emppic'>";						$query1 = "SELECT url FROM photos WHERE photoID=" . $item;						$result1 = $mysqli->query($query1);						if ($mysqli->errno) {							$output .=$mysqli->error;							exit();						}												while ($row1 = $result1->fetch_assoc()) {							foreach ($row1 as $type1 => $item1) {								$item1=trim($item1);																list($width, $height, $type1, $attr) = getimagesize("images/$item1");								$thumb_w = 200;								$new_w = $thumb_w;								$new_h = floor($height * ($thumb_w/$width));								if($new_h > 300) {									$new_h = 300;								}																$output .= "<img src='images/$item1' width='$new_w' height='$new_h' alt='images/$item1' />";							}						}												$output .= "</div>\n";					}					else if($type == "about"){						$output .= $item . "\n";					}			}						$output .= "</div>";		}		$output .="</div><div id=\"clear\">&nbsp;</div></div>";				return $output;	}?>