<?php		function frameworkGetContent() {				if (isset($_SESSION['logged_user'])) {			$olduser = $_SESSION['logged_user'];			unset($_SESSION['logged_user']);			session_destroy();		}				require("db.php");				$output = "";				$output.= "<div class=\"loginhead\">      			<img src=\"images/login.png\" alt=\"login\" />    			</div>";				if (!isset($_POST['username']) && !isset($_POST['password'])) {	  					$output.="<div class=\"output2\">								  <form id='logging' class=\"login\" action=\"index.php?page=login\" method=\"post\">						  <p>						  Username: <input type='text' name='username' id='username'/> <span class='msg' id='unamemsg'>*</span><br/>						  Password: <input type='password' name='password' id='password' /> <span class='msg' id='pwmsg'>*</span><br/>						 						  <input id='log' type=\"submit\" value=\"Login\" />						 </p>						  </form></div><div class=\"clear\">&nbsp;</div>";						  		} else { 					$username = mysql_real_escape_string(strip_tags($_POST['username']));					$hashpass = hash('sha256', (mysql_real_escape_string(strip_tags($_POST['password']))));										$result = $mysqli->query("SELECT * FROM users WHERE username = '$username' AND password = '$hashpass'");			if($result) {				if($result->num_rows != 0) {												  		$_SESSION['logged_user'] = $_POST['username'];					  		header("Location: http://info230.cs.cornell.edu/groups/Chips_on_a_stick/www/index.php?page=update");				}				else {	  					$output.="<div class=\"output2\">									<p id='instructions'>You did not login successfully</p>						  <form class=\"login\" action=\"index.php?page=login\" method=\"post\">						  <p>						  Username: <input type='text' name='username' id='username' /> <span class='msg' id='unamemsg'>*</span><br/>						  Password: <input type='password' name='password' id='password' /> <span class='msg' id='pwmsg'>*</span><br/>						 						  <input type='submit' value='Login' id='submit' />						  <span id='msgbox'></span>						 </p>						  </form></div>";						  				}			}		}	return $output;		}					