lt) {               		$output .= $mysqli->error;                	exit();            	}       			$pID="";       			while ($row = $result->fetch_assoc()){                	foreach ($row as $type => $item) {               			$pID=$item;            		}      			}								if ($check) {					//Error checking					$name = mysql_real_escape_string(strip_tags($_POST["name"]));					$desc = mysql_real_escape_string(strip_tags($_POST["desc"]));					$price = floatval(mysql_real_escape_string(strip_tags($_POST["price"])));										$query1 = "INSERT INTO menu(name, description, price, photoID) VALUES('$name', '$desc', $price, $pID)";					$mysqli->query($query1);				}			}						$output .= "<p> Item Name: <input type='text' name='name' id='name' /><span class='msg' id='namemsg'>*</span><br />					Item description: <input type='text' name='desc' id='desc' /><span class='msg' id='descmsg'>*</span><br />					Price: $<input type='text' name='price' id='price' /><span class='msg' id='pricemsg'>*</span><br />					Photo: <input type='file' name='file' id='photo' /><span class='msg' id='photomsg'>*</span>					</p>";						$output .= "<p>					<input type='submit' name='newItem' value='Add a New Menu Item' />					<span class='msg' id='submitmsg'>&nbsp;</span>					</p>				</form></div>";						//Delete a Menu Item		/* Pseudo Code					*/  		$output .= "<div id='deleteitem'><h2>Delete from the Menu</h2>					<form  id='deletemenu' action='?page=menu' method='post' >";						if(isset($_POST['deleteItem'])) {				$mID = $_POST["menu_item"];				$check = true;				if($check) {					$query0 = "SELECT photoID FROM menu WHERE menuID = $mID";					$result= $mysqli->query($query0);										$pid = "";					while ($row = $result->fetch_assoc()){                		foreach ($row as $type => $item) {               				$pid = $item;            			}      				}				            		$query1= "DELETE FROM menu WHERE menuID = $mID";            		$mysqli->query($query1);       